INSERT INTO "Books" VALUES(697,'圣域龙骑',' 小霍公子','http://image.qidian.com/books/3552964/3552964.jpg','http://www.qidian.com/Book/3552964.aspx','162869字','70章','起点中文网','15/09/12 17:18','第二季 青龙血脉　第68章 笑里藏刀','<p><p><p>                            &nbsp;&nbsp;&nbsp;&nbsp;
