INSERT INTO "Books" VALUES(854,'武帝的悠闲生活',' 京城冷','http://image.qidian.com/books/1.jpg','http://www.qidian.com/Book/3516987.aspx','0字','0章','起点中文网','15/06/19 09:35','','<p><p><p>                            &nbsp;&nbsp;&nbsp;&nbsp;
