INSERT INTO "Books" VALUES(945,'洪荒天鬼',' 隔日黄瓜','http://image.qidian.com/books/1246332/1246332.jpg','http://www.qidian.com/Book/1246332.aspx','347833字','107章','起点中文网','09/08/26 12:10','第三卷：人道大兴　说上两句吧','<p><p><p>                            &nbsp;&nbsp;&nbsp;&nbsp;
