INSERT INTO "Books" VALUES(984,'莽荒天界',' 情谊相许','http://image.qidian.com/books/3474869/3474869.jpg','http://www.qidian.com/Book/3474869.aspx','188515字','73章','起点中文网','15/06/26 20:26','正文　完结','<p><p><p>                            &nbsp;&nbsp;&nbsp;&nbsp;
