INSERT INTO "Books" VALUES(987,'九转归元',' PAST梁','http://image.qidian.com/books/1999151/1999151.jpg','http://www.qidian.com/Book/1999151.aspx','856244字','369章','起点中文网','12/08/20 21:24','第130章  天才战','<p><p><p>                            &nbsp;&nbsp;&nbsp;&nbsp;
