INSERT INTO "Books" VALUES(988,'夺天神工',' 笑论语','http://image.qidian.com/books/3479519/3479519.jpg','http://www.qidian.com/Book/3479519.aspx','192411字','75章','起点中文网','15/07/09 16:30','第二卷 塔楼　新书《独医》','<p><p><p>                            &nbsp;&nbsp;&nbsp;&nbsp;
